import React, { Component } from 'react';
import { Link} from 'react-router-dom';
import axios from 'axios';

interface Post {
  id: any;
  userId: number;
  title: string;
  body: string;
}

// export type Props = {
//   quotes?: any;
//   navigate: any;
//   params?: any;
// };
// class MovieQuote extends React.Component<Props> {
//   constructor(props: any) {
//     super(props);

//     this.state = {
//       quotes: [],
//     };
//   }


export type PostsProps  = {
  userId:sti;
}

interface PostsState {
  posts: Post[];
}

class Posts extends Component<PostsProps, PostsState> {
  state: PostsState = {
    posts: [],
  };

  componentDidMount():void {
    const { userId } = this.props;
    this.fetchPosts(userId);
  }

  fetchPosts = (userId: any) => {
    const currentURL = window.location.href;
    const currentID = currentURL.split("/");
    console.log(currentID,"=========>>>>>>");
    console.log(currentURL,"=========>>>>>>url")
    axios
      .get(`https://jsonplaceholder.typicode.com/users/${currentID[2] }/posts`)
      .then((response) => {
        this.setState({ posts: response.data });
      })
      .catch((error) => {
        console.error('Error fetching posts:', error);
      });
  };

  render() {
    const { posts } = this.state;

    return (
      <div>
        <h2>Posts</h2>
        <table className="table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>body</th>
            </tr>
          </thead>
          <tbody>
            {posts.map((post) => (
              <tr key={post.id}>
                <td>{post.id}</td>
                <td>{post.title}</td>
                <td>{post.body}</td>
                <td>
                  <Link to={`/posts/${post.userId}/comments`} className="btn btn-primary btn-sm">
                    View Comments
                  </Link>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <Link to={`/`} className="btn btn-primary btn-sm">
                   Back Home
                  </Link>
      </div>
    );
  }
}

export default Posts;
