import React, { Component } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { RootState } from "../../Redux/reducers/rootReducer";
import { fetchTodos } from "../../Redux/actions/actions";

interface Todo {
  id: any;
  userId:any;
  title: string;
  body: string;
  
}

interface TodoProps {
  potId: any;
  fetchTodos: (upostId: any) => void;
  todos: Todo[];
}

class Todo extends Component<TodoProps> {
  componentDidMount() {
    const { postId, fetchTodos } = this.props;
    fetchTodos(postId);
  }

  render() {
    const { todos } = this.props;

    return (
      <div>
        <h2>Todos</h2>
        <table className='table'>
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Body</th>
            </tr>
          </thead>
          <tbody>
            {todos.map((todo) => (
              <tr key={todo.id}>
                <td>{todo.id}</td>
                <td>{todo.title}</td>
                <td>{todo.body}</td>
                <td>
                  <Link
                    to={`/todos/${todo.postId}/comments`}
                    className='btn btn-primary btn-sm'
                  >
                    View Comments
                  </Link>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <Link to={`/`} className='btn btn-primary btn-sm'>
          Back Home
        </Link>
      </div>
    );
  }
}

const mapStateToProps = (state: RootState) => ({
  todos: state.todo.todos,
});

export default connect(mapStateToProps, { fetchTodos })(Todo);
