import axios from "axios";
import { Dispatch } from "redux";
// import Posts from "../../components/Post/Post";
import {
  FETCH_USERS_SUCCESS,
  ADD_USER_SUCCESS,
  UPDATE_USER_SUCCESS,
  DELETE_USER_SUCCESS,
  FETCH_POSTS_SUCCESS,
} from "../actionsType/actionsType";


// import { Post } from '../components/Posts';

// Action Creators
export const fetchUsersSuccess = (users: any) => ({
  type: FETCH_USERS_SUCCESS,
  payload: users,
});

export const addUserSuccess = (user: any) => ({
  type: ADD_USER_SUCCESS,
  payload: user,
});

export const updateUserSuccess = (user: any) => ({
  type: UPDATE_USER_SUCCESS,
  payload: user,
});

export const deleteUserSuccess = (userId: any) => ({
  type: DELETE_USER_SUCCESS,
  payload: userId,
});

// Thunk to fetch users
export const fetchUsers = () => {
  return (dispatch: any) => {
    axios
      .get("https://jsonplaceholder.typicode.com/users")
      .then((response) => {
        dispatch(fetchUsersSuccess(response.data));
      })
      .catch((error) => {
        console.error("Error fetching users:", error);
      });
  };
};

// Thunk to add user
export const addUser = (user: any) => {
  return (dispatch: any) => {
    axios
      .post("https://jsonplaceholder.typicode.com/users", user)
      .then((response) => {
        dispatch(addUserSuccess(response.data));
      })
      .catch((error) => {
        console.error("Error adding user:", error);
      });
  };
};

// Thunk to update user
export const updateUser = (user: any) => {
  return (dispatch: any) => {
    axios
      .put(`https://jsonplaceholder.typicode.com/users/${user.id}`, user)
      .then((response) => {
        dispatch(updateUserSuccess(response.data));
      })
      .catch((error) => {
        console.error("Error updating user:", error);
      });
  };
};

// Thunk to delete user
export const deleteUser = (userId: any) => {
  return (dispatch: any) => {
    axios
      .delete(`https://jsonplaceholder.typicode.com/users/${userId}`)
      .then(() => {
        dispatch(deleteUserSuccess(userId));
      })
      .catch((error) => {
        console.error("Error deleting user:", error);
      });
  };
};

//postsActions



export const fetchPostsSuccess = (posts: any) => {
  return {
    type: FETCH_POSTS_SUCCESS,
    payload: posts,
  };
};

export const fetchPosts = (userId: string) => {
  return (dispatch: Dispatch) => {
    axios
      .get(`https://jsonplaceholder.typicode.com/users/${userId}/posts`)
      .then((response) => {
        dispatch(fetchPostsSuccess(response.data));
      })
      .catch((error) => {
        console.error("Error fetching posts:", error);
      });
  };
};
