import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { fetchPosts } from '../../Redux/actions/actions';
import { RootState } from '../../Redux/reducers/rootReducer';

export type Post = {
  id: number;
  postId: number;
  title: string;
  body: string;
};

interface PostsProps {
  userId: string;
  fetchPosts: (userId: string) => void;
  posts: Post[];
}

 export class Posts extends Component<PostsProps> {
  componentDidMount(): void {
    const { userId, fetchPosts } = this.props;
    fetchPosts(userId);
  }

  render() {
    const { posts } = this.props;

    return (
      <div>
        <h2>Posts</h2>
        <table className="table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Body</th>
            </tr>
          </thead>
          <tbody>
            {posts.map((post) => (
              <tr key={post.id}>
                <td>{post.id}</td>
                <td>{post.title}</td>
                <td>{post.body}</td>
                <td>
                  <Link to={`/posts/${post.postId}/comments`} className="btn btn-primary btn-sm">
                    View Comments
                  </Link>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <Link to={`/`} className="btn btn-primary btn-sm">
          Back Home
        </Link>
      </div>
    );
  }
}

const mapStateToProps = (state:RootState) => ({
  posts: state.post.posts,
});

const mapDispatchToProps = {
  fetchPosts,
};

export default connect(mapStateToProps, mapDispatchToProps)(Posts);
